INSERT INTO `sys_oper_log` VALUES (1271, '项目管理', 1, 'com.wr.web.controller.module.ProjectController.add()', 'POST', 1, 'admin', NULL, '/system/project', '183.162.254.37', 'XX XX', '{\"createBy\":\"admin\",\"introduce\":\"<p>\\t<span style=\\\"background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);\\\">徽园位于安徽省合肥市</span>\\t<a href=\\\"https://baike.baidu.com/item/%E8%9C%80%E5%B1%B1%E5%8C%BA?fromModule=lemma_inlink\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"background-color: rgb(255, 255, 255); color: rgb(19, 110, 194);\\\">蜀山区</a>\\t<span style=\\\"background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);\\\">繁华大道276号，始建于1999年，占地面积20公顷，徽园东部</span>\\t<a href=\\\"https://baike.baidu.com/item/%E7%9A%96%E5%8C%97/5324230?fromModule=lemma_inlink\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"background-color: rgb(255, 255, 255); color: rgb(19, 110, 194);\\\">皖北</a>\\t<span style=\\\"background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);\\\">园区采用</span>\\t<a href=\\\"https://baike.baidu.com/item/%E7%9A%87%E5%AE%B6%E5%9B%AD%E6%9E%97/10902614?fromModule=lemma_inlink\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"background-color: rgb(255, 255, 255); color: rgb(19, 110, 194);\\\">皇家园林</a>\\t<span style=\\\"background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);\\\">建筑风格，西部皖南园区以</span>\\t<a href=\\\"https://baike.baidu.com/item/%E5%BE%BD%E6%B4%BE%E5%BB%BA%E7%AD%91/10640714?fromModule=lemma_inlink\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"background-color: rgb(255, 255, 255); color: rgb(19, 110, 194);\\\">徽派建筑</a>\\t<span style=\\\"background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);\\\">皖南水口园林为主要思路，中部有极具现代建筑风格的世纪桥和</span>\\t<a href=\\\"https://baike.baidu.com/item/%E4%B8%96%E7%BA%AA%E5%B9%BF%E5%9C%BA/4702056?fromModule=lemma_inlink\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"background-color: rgb(255, 255, 255); color: rgb(19, 110, 194);\\\">世纪广场</a>\\t<span style=\\\"background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);\\\">，全省17个城市在徽园均建有代表各自地方文化特色的标志性建筑物，既相对独立成园，又与相邻各园及整个园区相得益彰。</span></p>\",\"logoId\":43,\"logoUrl\":\"/profile/upload/2022/10/24/7b3a8c940702399407d7c91d143a98f5_20221024110903A004.jpeg\",\"params\":{},\"projectNam', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-24 11:09:30');
INSERT INTO `sys_oper_log` VALUES (1272, '项目故事', 1, 'com.wr.web.controller.module.StoryController.add()', 'POST', 1, 'admin', NULL, '/system/story', '183.162.254.37', 'XX XX', '{\"createBy\":\"admin\",\"detailed\":\"<p>\\t2014年9月6日-10月26日，徽园举办蜂鸣蝶舞欢乐嘉年华活动。活动以“万花丛中看蝶舞、黄金屋下闻蜂鸣”为主题，旨在通过提高市民对蜜蜂和蝴蝶的认识和热爱，共同维护和谐与文明的生态，营造人与自然、万物生灵相融共生的环境。<span style=\\\"color: rgb(51, 102, 204);\\\">&nbsp;[8]</span><span style=\\\"color: rgb(19, 110, 194);\\\">&nbsp;</span></p><p>\\t2015年春节期间，徽园举办民俗文化节，推出新年祈福、财神撒红包、庐剧演出、猜灯谜等活动，并上演“美猴王”真人情景秀。<span style=\\\"color: rgb(51, 102, 204);\\\">&nbsp;[28]</span><span style=\\\"color: rgb(19, 110, 194);\\\">&nbsp;</span></p><p>\\t2015年9月20日，徽园举办《中国梦·黄山魂全国山水画（中国画）作品展》。</p><p>\\t2015年9月18日，金色徽园文化惠民艺术月活动的序幕逐渐拉开，此次活动是2014年，徽园平价艺术月活动的延续，2015年，改名为金色徽园文化惠民艺术月，本次惠民艺术月活动期间共有4000余件作品参与展出，包括绘画作品150余件，书法作品200余件，摄影作品200余件，陶瓷作品100余件，玉器2000余件。<span style=\\\"color: rgb(51, 102, 204);\\\">&nbsp;[29]</span><span style=\\\"color: rgb(19, 110, 194);\\\">&nbsp;</span></p><p>\\t2016年9月10日，安徽省楷书精品展徽园书法馆开展，持续到9月30 日。共收到应征作品2000 余幅，121 幅作品入展。</p>\",\"params\":{},\"pictureId\":44,\"pictureUrl\":\"/profile/upload/2022/10/24/微信图片_20221024111840_20221024111906A011.jpg,/profile/upload/2022/10/24/1acaef7ef1687fb82f9b81a895772f2_20221024111912A012.jpg\",\"projectId\":7,\"region\":\"7\",\"sketch\":\"这个塔据说也叫“振风塔”，难道跟安庆迎江寺内的那个“振风塔”有什么渊源？\\n\\n\",\"sort\":\"0\",\"storyName\":\"振风塔\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-24 11:20:33');
INSERT INTO `sys_oper_log` VALUES (1273, '项目管理', 1, 'com.wr.web.controller.module.ProjectController.add()', 'POST', 1, 'admin', NULL, '/system/project', '183.162.254.37', 'XX XX', '{\"createBy\":\"admin\",\"introduce\":\"<p>党的二十大擘画了以中国式现代化推进中华民族伟大复兴的宏伟蓝图</p><p><img src=\\\"/prod-api/profile/upload/2022/10/24/97cf6239997883b016fd0f28b1567b41_20221024112132A014.jpeg\\\"></p>\",\"logoId\":45,\"logoUrl\":\"/profile/upload/2022/10/24/558d441f908198442eb9cdd1c388dae6_20221024112007A013.jpeg\",\"params\":{},\"projectName\":\"安徽渡江战役\",\"region\":\"7\",\"scenicId\":\"3\",\"scenicIdSet\":[3],\"sketch\":\"十年砥砺前行，征程波澜壮阔。回首过去十年，有涉滩之险，有爬坡之艰，有闯关之难，党和国家事业实现一系列突破性进展，取得一系列标志性成果。近1亿农村贫困人口实现脱贫，实现了小康这个中华民族的千年梦想；国内生产总值达114万亿元，经济实力实现历史性跃升；建成世界上规模最大的教育体系、社会保障体系、医疗卫生体系，人民生活全方位改善；生态环境保护发生历史性、转折性、全局性变化，我们的祖国天更蓝、山更绿、水更清……新时代十年的伟大变革，在党史、新中国史、改革开放史、社会主义发展史、中华民族发展史上具有里程碑意义。今天，中国人民的前进动力更加强大、奋斗精神更加昂扬、必胜信念更加坚定，焕发出更为强烈的历史自觉和主动精神，中国共产党和中国人民正信心百倍推进中华民族从站起来、富起来到强起来的伟大飞跃。\",\"sort\":\"1\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-24 11:21:41');
