INSERT INTO `sys_oper_log` VALUES (1434, '景点管理', 1, 'com.wr.web.controller.module.ScenicController.addStrategy()', 'POST', 1, 'admin', NULL, '/business/scenic/strategy', '127.0.0.1', '内网IP', '{\"createBy\":\"admin\",\"informationIsIntroduced\":\"迪士尼小镇是上海迪士尼度假区的大型购物餐饮娱乐区，毗邻上海迪士尼乐园，并将向公众免费开放。国际化的迷人街区令人流连忘返，游客可以在享用美食的同时观赏到上海迪士尼乐园的迷人景色。而位于“迪士尼小镇”的“华特迪士尼大剧院”，上演经典百老汇音乐剧，看剧单独售票。\",\"offByDefault\":\"1\",\"openDate\":[\"2022-10-11\",\"2022-10-27\"],\"openTime\":[\"08:00:00\",\"21:00:00\"],\"otherInstructions\":\"迪士尼小镇是上海迪士尼度假区的大型购物餐饮娱乐区，毗邻上海迪士尼乐园，并将向公众免费开放。国际化的迷人街区令人流连忘返，游客可以在享用美食的同时观赏到上海迪士尼乐园的迷人景色。而位于“迪士尼小镇”的“华特迪士尼大剧院”，上演经典百老汇音乐剧，看剧单独售票。\",\"params\":{\"dateStart\":\"2022-10-11\",\"dateEnd\":\"2022-10-27\",\"timeStart\":\"08:00\",\"timeEnd\":\"21:00\"},\"scenicId\":2,\"strategyName\":\"迪士尼小镇\",\"strategyPictures\":\"/profile/upload/2022/10/24/aaa_20221024223620A001.jpeg\",\"suggestPlayTime\":\"8\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-24 22:36:27');
INSERT INTO `sys_oper_log` VALUES (1435, '景点管理', 1, 'com.wr.web.controller.module.ScenicController.addStrategy()', 'POST', 1, 'admin', NULL, '/business/scenic/strategy', '127.0.0.1', '内网IP', '{\"createBy\":\"admin\",\"informationIsIntroduced\":\"“上海迪士尼乐园酒店”和“上海玩具总动员酒店”紧邻上海迪士尼乐园，优越的地理位置将迪士尼神奇之旅从白天延续到夜晚。两家酒店结合了迪士尼故事讲述的主题设计，并将为宾客提供温馨周到的服务、丰富多样的娱乐活动、美食飨宴、购物体验及各种休闲选择，带来蕴含中国魅力的迪士尼体验。如果在乐园中购买了商品，可以免费递送至其下榻的酒店中，另外度假区内还设有免费穿梭巴士和轮渡，往返于两座酒店和迪士尼乐园之间，交通十分方便。\",\"offByDefault\":\"1\",\"openDate\":[\"2022-10-12\",\"2022-10-21\"],\"openTime\":[\"08:00:00\",\"21:00:00\"],\"otherInstructions\":\"“上海迪士尼乐园酒店”和“上海玩具总动员酒店”紧邻上海迪士尼乐园，优越的地理位置将迪士尼神奇之旅从白天延续到夜晚。两家酒店结合了迪士尼故事讲述的主题设计，并将为宾客提供温馨周到的服务、丰富多样的娱乐活动、美食飨宴、购物体验及各种休闲选择，带来蕴含中国魅力的迪士尼体验。如果在乐园中购买了商品，可以免费递送至其下榻的酒店中，另外度假区内还设有免费穿梭巴士和轮渡，往返于两座酒店和迪士尼乐园之间，交通十分方便。\",\"params\":{\"dateStart\":\"2022-10-12\",\"dateEnd\":\"2022-10-21\",\"timeStart\":\"08:00\",\"timeEnd\":\"21:00\"},\"scenicId\":2,\"strategyName\":\"主题酒店\",\"strategyPictures\":\"/profile/upload/2022/10/24/b_20221024223650A002.jpg\",\"suggestPlayTime\":\"1\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-24 22:37:00');
