INSERT INTO `sys_oper_log` VALUES (1519, '活动发布', 1, 'com.wr.web.controller.module.EventReleaseController.add()', 'POST', 1, 'admin', NULL, '/business/eventRelease', '183.162.254.37', 'XX XX', '{\"content\":\"<p><span style=\\\"background-color: rgb(255, 255, 255); color: rgb(47, 47, 47);\\\">党的二十大擘画了以中国式现代化推进中华民族伟大复兴的宏伟蓝图，发出了为全面建设社会主义现代化国家、全面推进中华民族伟大复兴而团结奋斗的伟大号召。“新征程是充满光荣和梦想的远征。蓝图已经绘就，号角已经吹响。”习近平总书记在二十届中共中央政治局常委同中外记者见面时强调：“我们要踔厉奋发、勇毅前行，努力创造更加灿烂的明天。”总书记的铿锵话语，充满自信、充满坚定、充满情怀，激发亿万人民拼搏的干劲、点燃神州大地奋斗的豪情。</span></p>\",\"contentCategory\":\"0\",\"contentMainImage\":\"/profile/upload/2022/10/25/1663214575706692_20221025103852A023.jpg\",\"contentsTitle\":\"冬日活动\",\"createBy\":\"admin\",\"dateRange\":[\"2022-10-24\",\"2022-10-26\"],\"endingDate\":\"2022-10-26 00:00:00\",\"eventId\":21,\"params\":{},\"participatingUser\":\"所有人\",\"postSource\":\"0\",\"pushingArea\":\"2\",\"scenicIds\":1,\"startDate\":\"2022-10-24 00:00:00\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 10:39:05');
