INSERT INTO `sys_oper_log` VALUES (1521, '修改新闻资讯', 2, 'com.wr.web.controller.module.NewsController.edit()', 'PUT', 1, 'admin', NULL, '/system/news', '183.162.254.37', 'XX XX', '{\"clickThePv\":0,\"clickTheUv\":0,\"content\":\"<p>在全党全国各族人民迈上全面建设社会主义现代化国家新征程、向第二个百年奋斗目标进军的关键时刻，党的二十大明确提出了新时代新征程中国共产党的使命任务，对全面建设社会主义现代化国家、全面推进中华民族伟大复兴进行了战略谋划，对统筹推进“五位一体”总体布局、协调推进“四个全面”战略布局作出了全面部署。“蓝图已经绘就，号角已经吹响。”当前最重要的任务，就是撸起袖子加油干，心往一处想、劲往一处使，把党的二十大作出的重大决策部署付诸行动、见之于成效。</p><p><img src=\\\"/prod-api/profile/upload/2022/10/25/798d-78417fe84511d917d1745c693d6a0f08_20221025093327A056.jpg\\\"></p>\",\"contentCategory\":\"2\",\"contentSource\":\"人民日报\",\"contentStatus\":\"2\",\"contentsTitle\":\"“蓝图已经绘就，号角已经吹响”\",\"createBy\":\"admin\",\"createTime\":\"2022-10-25 10:06:54\",\"dateRange\":[\"2022-10-25\",\"2022-10-27\"],\"expirationOfValidity\":\"2022-10-27 00:00:00\",\"newId\":5,\"params\":{},\"picturePath\":\"/profile/upload/2022/10/25/9863-c060148fc69a813a66b9dff4273e583a_20221025093303A053.jpg\",\"picturePaths\":[\"/profile/upload/2022/10/25/9863-c060148fc69a813a66b9dff4273e583a_20221025093303A053.jpg\"],\"postSource\":\"1\",\"pushingArea\":\"5\",\"scenicId\":\"1\",\"scenicIds\":[1],\"scenicName\":\"北门老街\",\"startOfValidity\":\"2022-10-25 00:00:00\",\"tripartiteUrl\":\"wap.peopleapp.com/article/6900687/6763336\",\"updateBy\":\"admin\",\"updateTime\":\"2022-10-25 10:06:50\",\"workTime\":\"2022-10-25至2022-10-25\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 10:45:51');
INSERT INTO `sys_oper_log` VALUES (1522, '修改新闻资讯', 2, 'com.wr.web.controller.module.NewsController.edit()', 'PUT', 1, 'admin', NULL, '/system/news', '183.162.254.37', 'XX XX', '{\"clickThePv\":0,\"clickTheUv\":0,\"content\":\"<p>十年砥砺前行，征程波澜壮阔。回首过去十年，有涉滩之险，有爬坡之艰，有闯关之难，党和国家事业实现一系列突破性进展，取得一系列标志性成果。近1亿农村贫困人口实现脱贫，实现了小康这个中华民族的千年梦想；国内生产总值达114万亿元，经济实力实现历史性跃升；建成世界上规模最大的教育体系、社会保障体系、医疗卫生体系，人民生活全方位改善；生态环境保护发生历史性、转折性、全局性变化，我们的祖国天更蓝、山更绿、水更清……新时代十年的伟大变革，在党史、新中国史、改革开放史、社会主义发展史、中华民族发展史上具有里程碑意义。今天，中国人民的前进动力更加强大、奋斗精神更加昂扬、必胜信念更加坚定，焕发出更为强烈的历史自觉和主动精神，中国共产党和中国人民正信心百倍推进中华民族从站起来、富起来到强起来的伟大飞跃。</p>\",\"contentCategory\":\"0\",\"contentStatus\":\"2\",\"contentsTitle\":\"“我们要踔厉奋发、勇毅前行，努力创造更加灿烂的明天。”\",\"createBy\":\"admin\",\"createTime\":\"2022-10-25 10:04:11\",\"dateRange\":[\"2022-10-25\",\"2022-10-26\"],\"expirationOfValidity\":\"2022-10-26 00:00:00\",\"newId\":6,\"params\":{},\"picturePath\":\"/profile/upload/2022/10/25/cedf3667aba325f15d01be4c976fc3ae_20221025094629A001.jpeg\",\"picturePaths\":[\"/profile/upload/2022/10/25/cedf3667aba325f15d01be4c976fc3ae_20221025094629A001.jpeg\"],\"postSource\":\"0\",\"pushingArea\":\"1\",\"scenicId\":\"1\",\"scenicIds\":[1],\"scenicName\":\"北门老街\",\"startOfValidity\":\"2022-10-25 00:00:00\",\"updateBy\":\"admin\",\"updateTime\":\"2022-10-25 10:03:54\",\"workTime\":\"2022-10-25至2022-10-25\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 10:46:00');
