INSERT INTO `sys_oper_log` VALUES (1643, '活动发布', 1, 'com.wr.web.controller.module.EventReleaseController.add()', 'POST', 1, 'admin', NULL, '/business/eventRelease', '183.162.254.37', 'XX XX', '{\"content\":\"<p>       金秋十月，秋风送爽，在这喜庆祥和、硕果飘香的日子里，城市运营公司10月3日在北门老街举办“盛世华诞 举国同庆”系列活动—戏曲专场晚会，与市民共享喜悦，欢度国庆，喜迎二十大。演出邀请国家级非物质文化遗产皖南花鼓戏代表性传承人杨玉屏主导，在《戏曲大联唱》中拉开帷幕，用“打金枝”等经典曲目，生动演绎了当代戏剧传统文化的传承与创新，独具地方特色的表演风格及优美的声腔赢得了现场观众的阵阵掌声，给广大市民带来了一场视觉和听觉的盛宴。</p><p><br></p><p>       为庆祝祖国73周年华诞，营造“欢度国庆，喜迎二十大，永远跟党走，奋进新征程”的北门老街文化氛围，城市运营公司于10月1日、3日、5日晚在北门老街举办“盛世华诞 举国同庆”歌舞晚会，首晚演出在歌曲《红旗飘飘》中拉开帷幕，11个节目精彩连连，现场气氛热烈，全程欢声笑语不断，进一步增强了北门老街商户和群众的获得感、幸福感。</p><p><br></p><p><img src=\\\"/prod-api/profile/upload/2022/10/25/活动4.1_20221025145310A040.png\\\"></p><p><br></p><p><img src=\\\"/prod-api/profile/upload/2022/10/25/活动4.2_20221025145317A041.png\\\"></p><p><br></p><p><img src=\\\"/prod-api/profile/upload/2022/10/25/活动4.3_20221025145329A042.png\\\"></p><p><br></p><p><img src=\\\"/prod-api/profile/upload/2022/10/25/活动4.4_20221025145342A044.png\\\"></p><p><br></p><p><img src=\\\"/prod-api/profile/upload/2022/10/25/活动4.5_20221025145352A045.png\\\"></p><p><br></p><p><img src=\\\"/prod-api/profile/upload/2022/10/25/活动4.6_20221025145423A048.png\\\"></p><p><br></p><p><img src=\\\"/prod-api/profile/upload/2022/10/25/活动4.7_20221025145442A049.png\\\"></p><p><br></p><p><img src=\\\"/prod-api/profile/upload/2022/10/25/活动4.8_20221025145454A050.png\\\"></p><p><br></p>\",\"contentCategory\":\"2\",\"contentMainImage\":\"/profile/upload/2022/10/25/活动4.2_20221025145512A051.png\",\"contentsTitle\":\"“盛世华诞 举国同庆”系列活动—戏曲专场晚会\",\"createBy\":\"admin\",\"dateRange\":[\"2022-10-25\",\"2022-11-30\"],\"endingDate\":\"2022-11-30 00:00:00\",\"eventId\":28,\"params\":{},\"participatingUser\":\"所有\",\"postSource\":\"0\",\"pushingArea\":\"0\",\"scenicIds\":1,\"startDate\":\"2022-10-25 00:00:00\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 14:55:57');
