INSERT INTO `sys_oper_log` VALUES (1645, '创A专题', 1, 'com.wr.web.controller.module.GenATopicController.add()', 'POST', 1, 'admin', NULL, '/business/genATopic', '183.162.254.37', 'XX XX', '{\"content\":\"<p><span style=\\\"background-color: rgb(255, 255, 255); color: rgb(47, 47, 47);\\\">党的二十大擘画了以中国式现代化推进中华民族伟大复兴的宏伟蓝图，发出了为全面建设社会主义现代化国家、全面推进中华民族伟大复兴而团结奋斗的伟大号召。“新征程是充满光荣和梦想的远征。蓝图已经绘就，号角已经吹响。”习近平总书记在二十届中共中央政治局常委同中外记者见面时强调：“我们要踔厉奋发、勇毅前行，努力创造更加灿烂的明天。”总书记的铿锵话语，充满自信、充满坚定、充满情怀，激发亿万人民拼搏的干劲、点燃神州大地奋斗的豪情。</span></p><p><span style=\\\"background-color: rgb(255, 255, 255); color: rgb(47, 47, 47);\\\"><span class=\\\"ql-cursor\\\">﻿</span></span><img src=\\\"/prod-api/profile/upload/2022/10/25/798d-78417fe84511d917d1745c693d6a0f08_20221025150906A054.jpg\\\"></p>\",\"contentMainImage\":\"/profile/upload/2022/10/25/0e930e24a8f913232d664a57ec4007b5_20221025150833A053.jpeg\",\"contentsTitle\":\"老街的风景\",\"params\":{},\"pushingArea\":\"0\",\"scenicIds\":\"1\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 15:09:24');
