INSERT INTO `sys_oper_log` VALUES (1774, '项目文化', 2, 'com.wr.web.controller.module.ProjectCultureController.edit()', 'PUT', 1, 'admin', NULL, '/business/culture/edit', '183.162.254.37', 'XX XX', '{\"administrativeAreas\":\"0\",\"createBy\":\"admin\",\"culturalDescription\":\"<p><span style=\\\"color: rgb(34, 34, 34); background-color: rgb(255, 255, 255);\\\">夜市烧烤摊，浓浓烟火气</span></p><p><br></p><p><span style=\\\"background-color: rgb(255, 255, 255);\\\">这里，公园景观众多，有道叉河景观带、宛溪河景观带、双塔路樱花大道、凤凰城广场、小九华公园等。商业街总体量面积约25万平方米，由3个商业综合体及3个复古街区组成，区内步行街也分为3大主题地块，1号地块以台球俱乐部、茶吧、电竞类休闲娱乐为主，2号地块以社区便利商超、各种本土特色小吃、进口零食饮品为主，3号地块以特色餐饮街为主题聚集了中西式餐饮、音乐餐吧、火锅、烧烤等。此外，商业街还进行了别出心裁的亮化美化设计，即使是夜深人静之际，这里仍是流光溢彩，吸引着各路人群前往消费打卡。</span></p>\",\"cultureId\":3,\"cultureImages\":\"/profile/upload/2022/10/25/图片1_20221025172433A014.png\",\"cultureImagesList\":[\"/profile/upload/2022/10/25/图片1_20221025172433A014.png\"],\"params\":{},\"postStatus\":\"1\",\"projectId\":\"1\",\"projectName\":\"北门老街项目\",\"sticky\":\"1\",\"theNameOfTheCulture\":\"美食文化\",\"theSorting\":0,\"updateBy\":\"admin\",\"updateTime\":\"2022-10-25 17:29:05\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 17:31:40');
INSERT INTO `sys_oper_log` VALUES (1775, '项目文化', 2, 'com.wr.web.controller.module.ProjectCultureController.edit()', 'PUT', 1, 'admin', NULL, '/business/culture/edit', '183.162.254.37', 'XX XX', '{\"administrativeAreas\":\"0\",\"createBy\":\"admin\",\"culturalDescription\":\"<p><span style=\\\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34);\\\">夜市烧烤摊，浓浓烟火气</span></p><p><br></p><p><span style=\\\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34);\\\">在老街夜市，人流熙熙攘攘，好不热闹。</span></p><p><span style=\\\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34);\\\">“开展地摊夜市，一方面是为了提振夜经济，另一方面就是带动人气，给老街商家提供更多消费机会。”伍万和介绍，即便不是节假日，这里的夜晚也被照得通亮。浓浓的老街味，喜庆又吸睛，尤其给年轻人带来了一站式的吃喝玩乐体验。</span></p>\",\"cultureId\":3,\"cultureImages\":\"/profile/upload/2022/10/25/图片1_20221025172433A014.png\",\"cultureImagesList\":[\"/profile/upload/2022/10/25/图片1_20221025172433A014.png\"],\"params\":{},\"postStatus\":\"1\",\"projectId\":\"1\",\"projectName\":\"北门老街项目\",\"sticky\":\"1\",\"theNameOfTheCulture\":\"美食文化\",\"theSorting\":0,\"updateBy\":\"admin\",\"updateTime\":\"2022-10-25 17:31:40\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 17:32:50');
