INSERT INTO `sys_oper_log` VALUES (1812, '餐饮管理', 2, 'com.wr.web.controller.module.RestaurantController.edit()', 'PUT', 1, 'admin', NULL, '/business/restaurant/edit', '183.162.254.37', 'XX XX', '{\"administrativeRegion\":\"0\",\"businessHours\":[\"09:00:00\",\"21:00:00\"],\"diningCapacityStr\":\"20\",\"endTime\":\"21:00:00\",\"hotelDescription\":\"<p>一口黑铁锅，一块嫩豆腐，一颗腌白菜，一碗原汤，一碟蒜，一碟酱。锅开油热，倒入酸菜和蒜迅速翻炒，炒熟后倒入酱、原汤和豆腐炖煮，一道皖南名菜“千里飘香”便制作完成。“千里飘香”却臭味无限，哪怕嗅觉不敏感，也会被这臭味刺激得皱眉。虽是臭味无限，入口却细滑鲜香。就是这样一道让人又爱又恨、欲罢不能的菜肴，吸引了众多食客前来尝鲜</p>\",\"merchantContacts\":\"13788995487\",\"merchantKeyDescriptions\":\"徽州特色\",\"merchantPhotos\":\"/profile/upload/2022/10/25/徽知府-餐饮_20221025140919A016.png\",\"merchantPhotosList\":[\"/profile/upload/2022/10/25/徽知府-餐饮_20221025140919A016.png\"],\"nameOfHotel\":\"徽知府\",\"openingHours\":\"2022-10-02\",\"params\":{\"startTime\":\"09:00:00\",\"endTime\":\"21:00:00\"},\"preciseLocation\":\"安徽省宣城市宣州区澄江街道民生路大唐凤凰城\",\"pricePerCapita\":75,\"restaurantId\":7,\"startTime\":\"09:00:00\",\"typeOfCuisine\":\"1\",\"updateBy\":\"admin\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 19:19:20');
