INSERT INTO `sys_oper_log` VALUES (1815, '餐饮管理', 2, 'com.wr.web.controller.module.RestaurantController.edit()', 'PUT', 1, 'admin', NULL, '/business/restaurant/edit', '183.162.254.37', 'XX XX', '{\"administrativeRegion\":\"0\",\"businessHours\":[\"10:00:00\",\"22:00:00\"],\"diningCapacityStr\":\"100\",\"endTime\":\"22:00:00\",\"hotelDescription\":\"<p>徽菜，发源于绩溪，因徽商而兴，起于东晋，盛于唐宋，明清达于巅峰。其时，徽州营商人数之多，活动范围之广，资本之雄厚，皆居当时商团前列。</p><p>徽商纵横四海，却又偏爱家乡风味，其饮馔之丰盛，筵席之豪华，对徽菜的弘扬起到推波助澜之用。有人的地方就有江湖，有徽商的地方就有徽菜馆，由此诞生名噪一时的“徽馆业”。清代及民国间，绩溪旅外徽馆遍布中国，在长江中下游一带和大西南等地盛极一时。</p><p>就是在漫长的岁月里，经过历代名厨的辛勤创造、兼收并蓄，徽菜逐步形成了一个雅俗共赏、南北咸宜、独具一格、自成一体的著名菜系。</p>\",\"merchantContacts\":\"18855245781\",\"merchantKeyDescriptions\":\"舌尖上的美食\",\"merchantPhotos\":\"/profile/upload/2022/10/25/徽碗_20221025135245A007.png\",\"merchantPhotosList\":[\"/profile/upload/2022/10/25/徽碗_20221025135245A007.png\"],\"nameOfHotel\":\"徽碗\",\"openingHours\":\"2022-09-30\",\"params\":{\"startTime\":\"10:00:00\",\"endTime\":\"22:00:00\"},\"preciseLocation\":\"安徽省宣城市宣州区澄江街道民生路大唐凤凰城\",\"pricePerCapita\":49,\"restaurantId\":5,\"startTime\":\"10:00:00\",\"typeOfCuisine\":\"1\",\"updateBy\":\"admin\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 19:20:42');
INSERT INTO `sys_oper_log` VALUES (1816, '餐饮管理', 2, 'com.wr.web.controller.module.RestaurantController.edit()', 'PUT', 1, 'admin', NULL, '/business/restaurant/edit', '183.162.254.37', 'XX XX', '{\"administrativeRegion\":\"0\",\"businessHours\":[\"09:00:00\",\"21:00:00\"],\"diningCapacityStr\":\"200\",\"endTime\":\"21:00:00\",\"hotelDescription\":\"<p>徽粤楼，以特色徽菜和精品粤菜为主。创新推出多款景点菜式，继承和发扬了传统徽粤菜文化，以特色香味发挥的淋漓尽致。还为顾客提供了既优雅又时尚，既古典又现代，既传统又创新，完美融合东西文化、洋溢和艺术气息的美好空间。</p>\",\"merchantContacts\":\"18874569745\",\"merchantKeyDescriptions\":\"尝试不一样的安徽粤菜\",\"merchantPhotos\":\"/profile/upload/2022/10/25/徽粤楼-餐饮_20221025140411A015.png\",\"merchantPhotosList\":[\"/profile/upload/2022/10/25/徽粤楼-餐饮_20221025140411A015.png\"],\"nameOfHotel\":\"徽粤楼\",\"openingHours\":\"2022-10-01\",\"params\":{\"startTime\":\"09:00:00\",\"endTime\":\"21:00:00\"},\"preciseLocation\":\"安徽省宣城市宣州区澄江街道徽粤楼酒店安徽省宣城市第三中学\",\"pricePerCapita\":100,\"restaurantId\":6,\"startTime\":\"09:00:00\",\"typeOfCuisine\":\"1\",\"updateBy\":\"admin\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 19:20:56');
INSERT INTO `sys_oper_log` VALUES (1817, '餐饮管理', 2, 'com.wr.web.controller.module.RestaurantController.edit()', 'PUT', 1, 'admin', NULL, '/business/restaurant/edit', '183.162.254.37', 'XX XX', '{\"administrativeRegion\":\"0\",\"businessHours\":[\"11:00:00\",\"23:59:00\"],\"diningCapacityStr\":\"500\",\"endTime\":\"23:59:00\",\"hotelDescription\":\"<p>\\t<span style=\\\"color: rgb(51, 51, 51);\\\">潮汕牛肉火锅成名于上世纪40年代，传统做法是将沙茶酱加入锅中，用浓汤做锅底，后来逐渐化繁为简，涮牛肉的锅底只剩下牛骨清汤和白萝卜，更有利于尝出牛肉本来的味道，潮汕美食最讲究‘大味至淡’，从历史角度看，大都是悄无声息做减法的过程。</span></p><p>\\t<span style=\\\"color: rgb(51, 51, 51);\\\">潮汕的牛肉火锅，牛肉都是现宰现卖，把“鲜”字发挥到极致。牛肉部位不同，质地不同，需要涮的时间不同，口感和味道也大相径庭。</span></p>\",\"merchantContacts\":\"18845657456\",\"merchantKeyDescriptions\":\"把眼睛留给风光，把体重留给潮牛\",\"merchantPhotos\":\"/profile/upload/2022/10/25/潮牛掌柜-餐饮_20221025145338A043.png\",\"merchantPhotosList\":[\"/profile/upload/2022/10/25/潮牛掌柜-餐饮_20221025145338A043.png\"],\"nameOfHotel\":\"潮牛掌柜\",\"openingHours\":\"2022-10-18\",\"params\":{\"startTime\":\"11:00:00\",\"endTime\":\"23:59:00\"},\"preciseLocation\":\"安徽省宣城市宣州区澄江街道潮牛掌柜潮汕鲜牛火锅(北门老街店)安徽省宣城市第三中学\",\"pricePerCapita\":80,\"restaurantId\":9,\"startTime\":\"11:00:00\",\"typeOfCuisine\":\"0\",\"updateBy\":\"admin\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-25 19:21:09');
