INSERT INTO `sys_oper_log` VALUES (2018, '餐饮管理', 2, 'com.wr.web.controller.module.RestaurantController.edit()', 'PUT', 1, 'admin', NULL, '/business/restaurant/edit', '183.162.254.37', 'XX XX', '{\"administrativeRegion\":\"0\",\"businessHours\":[\"10:00:00\",\"22:00:00\"],\"diningCapacityStr\":\"100\",\"endTime\":\"22:00:00\",\"hotelDescription\":\"<p>徽菜，发源于绩溪，因徽商而兴，起于东晋，盛于唐宋，明清达于巅峰。其时，徽州营商人数之多，活动范围之广，资本之雄厚，皆居当时商团前列。</p><p>徽商纵横四海，却又偏爱家乡风味，其饮馔之丰盛，筵席之豪华，对徽菜的弘扬起到推波助澜之用。有人的地方就有江湖，有徽商的地方就有徽菜馆，由此诞生名噪一时的“徽馆业”。清代及民国间，绩溪旅外徽馆遍布中国，在长江中下游一带和大西南等地盛极一时。</p><p>就是在漫长的岁月里，经过历代名厨的辛勤创造、兼收并蓄，徽菜逐步形成了一个雅俗共赏、南北咸宜、独具一格、自成一体的著名菜系</p>\",\"merchantContacts\":\"18855245781\",\"merchantKeyDescriptions\":\"舌尖上的美食\",\"merchantPhotos\":\"/profile/upload/2022/10/26/adaf2edda3cc7cd98d103586154d363fb80e7bec3b12_20221026112552A001.jpg\",\"merchantPhotosList\":[\"/profile/upload/2022/10/26/adaf2edda3cc7cd98d103586154d363fb80e7bec3b12_20221026112552A001.jpg\"],\"nameOfHotel\":\"徽碗\",\"openingHours\":\"2022-09-30\",\"params\":{\"startTime\":\"10:00:00\",\"endTime\":\"22:00:00\"},\"preciseLocation\":\"安徽省宣城市宣州区澄江街道民生路大唐凤凰城\",\"pricePerCapita\":49,\"restaurantId\":5,\"startTime\":\"10:00:00\",\"typeOfCuisine\":\"1\",\"updateBy\":\"admin\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-26 14:11:18');
INSERT INTO `sys_oper_log` VALUES (2019, '餐饮管理', 2, 'com.wr.web.controller.module.RestaurantController.edit()', 'PUT', 1, 'admin', NULL, '/business/restaurant/edit', '183.162.254.37', 'XX XX', '{\"administrativeRegion\":\"0\",\"businessHours\":[\"10:00:00\",\"22:00:00\"],\"diningCapacityStr\":\"20\",\"endTime\":\"22:00:00\",\"hotelDescription\":\"<p><span style=\\\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34);\\\">一提到火锅，可能很多吃货们都开始忍不住要流口水了，毕竟火锅的配菜非常多，基本上什么食材都能够吃到。对于口味清淡的朋友们来说，骨头火锅是最好的选择了，鲜香的口感、浓郁的汤汁，恋恋不舍，让人垂涎欲滴。食材经济实惠、制作简单。天气已逐渐转凉，在这寒意渐浓的时节，来上一顿火锅绝对算得上是盛宴</span></p>\",\"merchantContacts\":\"15245451254\",\"merchantKeyDescriptions\":\"营养大排档\",\"merchantPhotos\":\"/profile/upload/2022/10/25/尚记骨头王-餐饮_20221025141954A017.png,/profile/upload/2022/10/25/尚记骨头王-餐饮_20221025142129A019.png\",\"merchantPhotosList\":[\"/profile/upload/2022/10/25/尚记骨头王-餐饮_20221025141954A017.png\",\"/profile/upload/2022/10/25/尚记骨头王-餐饮_20221025142129A019.png\"],\"nameOfHotel\":\"尚记骨头王\",\"openingHours\":\"2022-10-03\",\"params\":{\"startTime\":\"10:00:00\",\"endTime\":\"22:00:00\"},\"preciseLocation\":\"安徽省宣城市宣州区澄江街道民生路大唐凤凰城\",\"pricePerCapita\":20,\"restaurantId\":8,\"startTime\":\"10:00:00\",\"typeOfCuisine\":\"1\",\"updateBy\":\"admin\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-26 14:11:27');
