INSERT INTO `sys_oper_log` VALUES (2289, '景点管理', 1, 'com.wr.web.controller.module.ScenicController.add()', 'POST', 1, 'admin', NULL, '/business/scenic', '183.162.254.37', 'XX XX', '{\"createBy\":\"admin\",\"describeS\":\"度假胜地\",\"introduce\":\"<p>党的十八大以来，我们党勇于进行理论探索和创新，以全新的视野深化对共产党执政规律、社会主义建设规律、人类社会发展规律的认识，取得重大理论创新成果，集中体现为习近平新时代中国特色社会主义思想。习近平新时代中国特色社会主义思想是当代中国马克思主义、二十一世纪马克思主义，是中华文化和中国精神的时代精华，实现了马克思主义中国化时代化新的飞跃，为新时代党和国家事业发展提供了根本遵循。</p>\",\"params\":{\"startDate\":\"2022-10-01\",\"endDate\":\"2022-11-30\",\"startTime\":\"08:00\",\"endTime\":\"21:00\"},\"region\":\"3\",\"scenicAdd\":\"安徽省合肥市长丰县双凤开发区梅冲湖公园\",\"scenicLevel\":\"3\",\"scenicName\":\"景点C\",\"scenicPhone\":\"15911110000\",\"scenicPictureId\":\"/profile/upload/2022/10/27/97cf6239997883b016fd0f28b1567b41_20221027105301A007.jpeg,/profile/upload/2022/10/27/1ada67cb62a0e087964800f5701879c7_20221027105313A008.jpeg,/profile/upload/2022/10/27/aac1b35b15ddd43f3f5c7cfb48695237_20221027105324A009.jpeg,/profile/upload/2022/10/27/cedf3667aba325f15d01be4c976fc3ae_20221027105329A010.jpeg,/profile/upload/2022/10/27/1658893861035_20221027105340A011.png\",\"sticky\":\"1\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-27 10:54:07');
