INSERT INTO `sys_oper_log` VALUES (2451, '餐饮管理', 2, 'com.wr.web.controller.module.RestaurantController.edit()', 'PUT', 1, 'admin', NULL, '/business/restaurant/edit', '183.162.254.37', 'XX XX', '{\"administrativeRegion\":\"0\",\"businessHours\":[\"11:00:00\",\"23:59:00\"],\"diningCapacityStr\":\"500\",\"endTime\":\"23:59:00\",\"hotelDescription\":\"<p><span style=\\\"color: rgb(51, 51, 51);\\\">潮汕牛肉火锅成名于上世纪40年代，传统做法是将沙茶酱加入锅中，用浓汤做锅底，后来逐渐化繁为简，涮牛肉的锅底只剩下牛骨清汤和白萝卜，更有利于尝出牛肉本来的味道，潮汕美食最讲究‘大味至淡’，从历史角度看，大都是悄无声息做减法的过程。</span></p><p>\\t<span style=\\\"color: rgb(51, 51, 51);\\\">潮汕的牛肉火锅，牛肉都是现宰现卖，把“鲜”字发挥到极致。牛肉部位不同，质地不同，需要涮的时间不同，口感和味道也大相径庭。</span><img src=\\\"/prod-api/profile/upload/2022/10/28/图片11_20221028114930A011.png\\\"><img src=\\\"/prod-api/profile/upload/2022/10/28/图片12_20221028114934A012.png\\\"><img src=\\\"/prod-api/profile/upload/2022/10/28/图片13_20221028114940A013.png\\\"><img src=\\\"/prod-api/profile/upload/2022/10/28/图片14_20221028114944A014.png\\\"><img src=\\\"/prod-api/profile/upload/2022/10/28/图片15_20221028114947A015.png\\\"><img src=\\\"/prod-api/profile/upload/2022/10/28/图片16_20221028114951A016.png\\\"><img src=\\\"/prod-api/profile/upload/2022/10/28/图片17_20221028114955A017.png\\\"><img src=\\\"/prod-api/profile/upload/2022/10/28/图片18_20221028114959A018.png\\\"><img src=\\\"/prod-api/profile/upload/2022/10/28/图片19_20221028115002A019.png\\\"></p>\",\"merchantContacts\":\"18845657456\",\"merchantKeyDescriptions\":\"把眼睛留给风光，把体重留给潮牛\",\"merchantPhotos\":\"/profile/upload/2022/10/25/潮牛掌柜-餐饮_20221025145338A043.png\",\"merchantPhotosList\":[\"/profile/upload/2022/10/25/潮牛掌柜-餐饮_20221025145338A043.png\"],\"nameOfHotel\":\"潮牛掌柜\",\"openingHours\":\"2022-10-18\",\"params\":{\"startTime\":\"11:00:00\",\"endTime\":\"23:59:00\"},\"preciseLocation\":\"安徽省宣城市宣州区澄江街道潮牛掌柜潮汕鲜牛火锅(北门老街店)安徽省宣城市第三中学\",\"pricePerCapita\":80,\"restaurantId\":9,\"startTime\":\"11:00:00\",\"typeOfCuisine\":\"0\",\"updateBy\":\"admin\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-28 11:50:08');
