INSERT INTO `sys_oper_log` VALUES (2464, '景点管理', 2, 'com.wr.web.controller.module.ScenicController.edit()', 'PUT', 1, 'admin', NULL, '/business/scenic/edit', '127.0.0.1', '内网IP', '{\"describeS\":\"宣城市北门老街位于安徽省宣城市核心城区，东至澄江路，西至拱极路，南至槐树巷路，北至双塔路。整条商业街全长1250米，宽度327米，专属机动车停车场3个，停车位1134个。\",\"fullYear\":\"全年\",\"introduce\":\"<p>﻿    北门老街千年历史的承载，古色古香的一条历史文化老街，地理位置优越，位于宣城市区主城区一环内外，曾经是水运码头的集散地、最繁华的闹市；商业集中，且呈带状分布，既有小店铺也有大商业；具有鲜明的建筑特色，既有现代风格的商业街区，也有新中式风格的商业街区；人口集中，本区域人口达7万余人，1.5公里范围约15余万人；历史文化遗迹丰富，有原址保护的丰乐桥、移址复建的桃源桥、别士桥、水门等；文化底蕴深厚，有李白送友人的别士桥码头、宣州十景之一的澄江夜月，基督教堂、小九华佛寺坐落于此；公园景观众多，区内有道叉河景观带、宛溪河景观带、双塔路樱花大道、凤凰城广场、小九华公园等。</p><p class=\\\"ql-align-center\\\">\\t商业街总体量面积约25万m²,由三个分别为5.8万m²、4.2万m²、2.4万m²的商业综合体、三个复古街区组团及三条步行街。形成了以餐饮、特色小吃、休闲娱乐、购物、住宿为主，其他业态为补充的一站式综合商业街。城建集团为了打造整体特色商业街，分别在别士桥、三元、文星楼商业步行街做了亮化工程，商业街的亮化设计可以营造出商业街特殊的商业文化氛围，以最现代的科技照明运作，结合创新的亮化设计把商业街的人文特色、服务特色完整的呈现在顾客面前，煽动人们的消费欲望，起到直接推动市场经济的作用。</p><p class=\\\"ql-align-center\\\"><br></p>\",\"openDaysEnd\":\"2023-11-24\",\"openDaysList\":[\"2022-09-27\",\"2023-11-24\"],\"openDaysStart\":\"2022-09-27\",\"openEndTime\":\"21:00:00\",\"openStartTime\":\"08:00:00\",\"openTimeList\":[\"08:00:00\",\"21:00:00\"],\"params\":{\"startDate\":\"2022-09-27\",\"endDate\":\"2023-11-24\",\"startTime\":\"08:00:00\",\"endTime\":\"21:00:00\"},\"region\":\"0\",\"scenicAdd\":\"安徽省宣城市宣州区澄江街道民生路\",\"scenicId\":1,\"scenicLevel\":\"5\",\"scenicName\":\"北门老街\",\"scenicPhone\":\"131222255551\",\"scenicPictureId\":\"/profile/upload/2022/10/25/图片1_20221025163009A078.png,/profile/upload/2022/10/25/北门新貌-老街故事_20221025191601A004.png,/profile/upload/2022/10/25/首页2_20221025191608A005.png\",\"scenicPictureList\":[\"/profile/upload/2022/10/25/图片1_20221025163009A078.png\",\"/profile/upload/2022/10/25/北门新貌-老街故事_20221025191601A004.png\",\"/profile/upload/2022/10/25/首页2_20221025191608A005.png\"],\"scenicVideoId\":\"/profile/upload/2022/10/26/75f16a0b8508ed57f33231f55b792a3f_20221026153800A042.mp4\",\"scenicVideoList\":[\"/profile/upload/2022/10/26/75f16a0b8508ed57f33231f