INSERT INTO `sys_oper_log` VALUES (2484, '添加新闻资讯', 1, 'com.wr.web.controller.module.NewsController.add()', 'POST', 1, 'admin', NULL, '/system/news', '183.162.254.37', 'XX XX', '{\"content\":\"<p><span style=\\\"background-color: rgb(241, 241, 241); color: rgb(92, 92, 92);\\\">10月26日，31个省份和新疆兵团新增确诊病例231例，其中境外输入病例38例，本土193例；无新增死亡病例，累计死亡5226例；无症状感染者新增1033例，尚在观察无症状感染者15557例；现有确诊病例3666例，重症病例18例</span></p>\",\"contentCategory\":\"0\",\"contentSource\":\"财经网\",\"contentsTitle\":\"全国新冠累计确诊258398例 累计接种新冠疫苗超34.39亿剂次\",\"dateRange\":[\"2022-10-28\",\"2022-10-31\"],\"expirationOfValidity\":\"2022-10-31 00:00:00\",\"params\":{},\"picturePath\":\"/profile/upload/2022/10/28/微信图片_20221028154634_20221028154656A065.jpg\",\"postSource\":\"1\",\"pushingArea\":\"0\",\"scenicId\":\"1,4\",\"scenicIds\":[1,4],\"startOfValidity\":\"2022-10-28 00:00:00\",\"tripartiteUrl\":\"www.caixin.com/2022-10-27/101956713.html\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-28 15:46:59');
