INSERT INTO `sys_oper_log` VALUES (2534, '景点管理', 1, 'com.wr.web.controller.module.ScenicController.add()', 'POST', 1, 'admin', NULL, '/business/scenic', '183.162.254.37', 'XX XX', '{\"createBy\":\"admin\",\"describeS\":\"北门老街千年历史的承载，古色古香的一条历史文化老街，地理位置优越，位于宣城市区主城区一环内外，曾经是水运码头的集散地、最繁华的闹市\",\"introduce\":\"<p>\\t宣城市北门老街位于安徽省宣城市核心城区，东至澄江路，西至拱极路，南至槐树巷路，北至双塔路。整条商业街全长1250米，宽度327米，专属机动车停车场3个，停车位1134个。</p><p>\\t北门老街千年历史的承载，古色古香的一条历史文化老街，地理位置优越，位于宣城市区主城区一环内外，曾经是水运码头的集散地、最繁华的闹市；商业集中，且呈带状分布，既有小店铺也有大商业；具有鲜明的建筑特色，既有现代风格的商业街区，也有新中式风格的商业街区；人口集中，本区域人口达7万余人，1.5公里范围约15余万人；历史文化遗迹丰富，有原址保护的丰乐桥、移址复建的桃源桥、别士桥、水门等；文化底蕴深厚，有李白送友人的别士桥码头、宣州十景之一的澄江夜月，基督教堂、小九华佛寺坐落于此；公园景观众多，区内有道叉河景观带、宛溪河景观带、双塔路樱花大道、凤凰城广场、小九华公园等。</p>\",\"params\":{\"startDate\":\"2022-10-31\",\"endDate\":\"2022-11-05\",\"startTime\":\"08:00\",\"endTime\":\"20:00\"},\"region\":\"0\",\"scenicAdd\":\"安徽省宣城市宣州区溪口镇\",\"scenicLevel\":\"2\",\"scenicName\":\"老街景点\",\"scenicPhone\":\"055188430556\",\"scenicPictureId\":\"/profile/upload/2022/10/31/景点1_20221031112444A001.png\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 11:25:43');
