INSERT INTO `sys_oper_log` VALUES (2570, '创A专题', 1, 'com.wr.web.controller.module.GenATopicController.add()', 'POST', 1, 'admin', NULL, '/business/genATopic', '183.162.254.37', 'XX XX', '{\"content\":\"<p>金秋十月，秋风送爽，在这喜庆祥和、硕果飘香的日子里，城市运营公司10月3日在北门老街举办“盛世华诞 举国同庆”系列活动—戏曲专场晚会，与市民共享喜悦，欢度国庆，喜迎二十大。演出邀请国家级非物质文化遗产皖南花鼓戏代表性传承人杨玉屏主导，在《戏曲大联唱》中拉开帷幕，用“打金枝”等经典曲目，生动演绎了当代戏剧传统文化的传承与创新，独具地方特色的表演风格及优美的声腔赢得了现场观众的阵阵掌声，给广大市民带来了一场视觉和听觉的盛宴。</p><p><img src=\\\"/prod-api/profile/upload/2022/10/31/戏曲_20221031132935A027.png\\\"></p><p>首晚演出在歌曲《红旗飘飘》中拉开帷幕</p><p><img src=\\\"/prod-api/profile/upload/2022/10/31/戏曲4_20221031133049A029.png\\\"></p>\",\"contentMainImage\":\"/profile/upload/2022/10/31/戏剧_20221031132858A026.png\",\"contentsTitle\":\"“盛世华诞 举国同庆”系列活动—戏曲专场晚会\",\"params\":{},\"pushingArea\":\"0\",\"scenicIds\":\"1\",\"scenicList\":[\"1\"]}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 13:30:51');
