INSERT INTO `sys_oper_log` VALUES (2591, '项目文化', 2, 'com.wr.web.controller.module.ProjectCultureController.edit()', 'PUT', 1, 'admin', NULL, '/business/culture/edit', '183.162.254.37', 'XX XX', '{\"administrativeAreas\":\"1\",\"createBy\":\"admin\",\"culturalDescription\":\"<p class=\\\"ql-align-justify\\\">“从出生我就住在北门，小时候每次从繁华的市中心回到家里，都会有一种荒凉寂寞的感觉。”在90年出生的孙敏看来，这里已经褪去了过往的荣光，没有林立的商铺，也没有来往的车辆。“每次我想买个东西还要走好长一段路，才能到小超市。”老街区给孙敏最直观的感觉就是不方便。</p>\",\"cultureId\":1,\"cultureImages\":\"/profile/upload/2022/10/31/活动2.4_20221031132449A021.png\",\"cultureImagesList\":[\"/profile/upload/2022/10/31/活动2.4_20221031132449A021.png\"],\"params\":{},\"postStatus\":\"1\",\"projectId\":\"2\",\"projectName\":\"老北门\",\"theNameOfTheCulture\":\"艺术文化\",\"theSorting\":\"2\",\"updateBy\":\"admin\",\"updateTime\":\"2022-10-31 13:42:59\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 13:46:55');
INSERT INTO `sys_oper_log` VALUES (2592, '项目文化', 2, 'com.wr.web.controller.module.ProjectCultureController.edit()', 'PUT', 1, 'admin', NULL, '/business/culture/edit', '183.162.254.37', 'XX XX', '{\"administrativeAreas\":\"1\",\"createBy\":\"admin\",\"culturalDescription\":\"<p>夜市烧烤摊，浓浓烟火气。</p>\",\"cultureId\":2,\"cultureImages\":\"/profile/upload/2022/10/31/文化1_20221031132824A024.png\",\"cultureImagesList\":[\"/profile/upload/2022/10/31/文化1_20221031132824A024.png\"],\"params\":{},\"postStatus\":\"1\",\"projectId\":\"2\",\"projectName\":\"老北门\",\"theNameOfTheCulture\":\"美食之旅\",\"theSorting\":\"3\",\"updateBy\":\"admin\",\"updateTime\":\"2022-10-31 13:43:23\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 13:49:28');
INSERT INTO `sys_oper_log` VALUES (2593, '项目文化', 2, 'com.wr.web.controller.module.ProjectCultureController.edit()', 'PUT', 1, 'admin', NULL, '/business/culture/edit', '183.162.254.37', 'XX XX', '{\"administrativeAreas\":\"1\",\"createBy\":\"admin\",\"culturalDescription\":\"<p>回溯历史长河，北门老街因为紧挨徽商水道宛溪河，这里曾是繁华的商贸街区，除了各类古建筑，还有老码头。</p>\",\"cultureId\":4,\"cultureImages\":\"/profile/upload/2022/10/31/文化3_20221031133336A033.png\",\"cultureImagesList\":[\"/profile/upload/2022/10/31/文化3_20221031133336A033.png\"],\"params\":{},\"postStatus\":\"1\",\"projectId\":\"2\",\"projectName\":\"老北门\",\"theNameOfTheCulture\":\"历史长河\",\"theSorting\":\"4\",\"updateBy\":\"admin\",\"updateTime\":\"2022-10-31 13:43:36\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 13:49:47');
INSERT INTO `sys_oper_log` VALUES (2594, '项目文化', 2, 'com.wr.web.controller.module.ProjectCultureController.edit()', 'PUT', 1, 'admin', NULL, '/business/culture/edit', '183.162.254.37', 'XX XX', '{\"administrativeAreas\":\"1\",\"createBy\":\"admin\",\"culturalDescription\":\"<p>时光荏苒，老街早已褪去昔日的热闹和喧嚣，陈旧的建筑街区、落后的基础设施、逼仄的巷口空间……取而代之的是宁静与安详。</p>\",\"cultureId\":3,\"cultureImages\":\"/profile/upload/2022/10/31/文化2_20221031133124A030.png\",\"cultureImagesList\":[\"/profile/upload/2022/10/31/文化2_20221031133124A030.png\"],\"params\":{},\"postStatus\":\"1\",\"projectId\":\"2\",\"projectName\":\"老北门\",\"theNameOfTheCulture\":\"民俗风情\",\"theSorting\":\"1\",\"updateBy\":\"admin\",\"updateTime\":\"2022-10-31 13:41:51\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 13:50:04');
