INSERT INTO `sys_oper_log` VALUES (2597, '景点管理', 2, 'com.wr.web.controller.module.ScenicController.editStrategy()', 'PUT', 1, 'admin', NULL, '/business/scenic/strategy/edit', '183.162.254.37', 'XX XX', '{\"dateList\":[\"2022-10-31\",\"2022-11-19\"],\"imgList\":[\"/profile/upload/2022/10/31/图片1_20221031114842A008.png\"],\"informationIsIntroduced\":\"夜市烧烤摊，浓浓烟火气。\\n不仅如此，在疫情防控常态化的当下，宣城市还出台了惠企政策，针对全市国有企业旗下的资产实行政策减免，今年二三四月实行房租全额减免，五六月房租减半征收，“真金白银”为企业减负。\",\"offByDefault\":\"1\",\"openDate\":[\"2022-10-31\",\"2022-11-19\"],\"openDateEnd\":\"2022-11-19\",\"openDateStart\":\"2022-10-31\",\"openTime\":[\"08:00:00\",\"17:30:00\"],\"openTimeEnd\":\"17:30:00\",\"openTimeStart\":\"08:00:00\",\"otherInstructions\":\"“整条老街商铺累计减免房租300余万元。”宋海明接过话茬，政府的扶持好比一剂强心针，就拿宣城市文旅局量身打造了“寻味皖南 食尚宣城”美食周来说，虽然活动只有短短8天时间，但期间累计发放消费补贴13万余元，拉动消费200万元，惠及群众近10万人次，带动当地餐饮行业经济增长50%以上。\",\"params\":{\"dateStart\":\"2022-10-31\",\"dateEnd\":\"2022-11-19\",\"timeStart\":\"08:00:00\",\"timeEnd\":\"17:30:00\"},\"region\":\"0\",\"scenicId\":1,\"scenicName\":\"老街景点\",\"strategyId\":2,\"strategyName\":\"夜市烧烤摊\",\"strategyPictures\":\"/profile/upload/2022/10/31/图片1_20221031114842A008.png,/profile/upload/2022/10/31/图片3_20221031135129A039.png\",\"suggestPlayTime\":\"0\",\"timeList\":[\"08:00:00\",\"17:30:00\"],\"updateBy\":\"admin\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 13:51:31');
