INSERT INTO `sys_oper_log` VALUES (2685, '创A专题', 2, 'com.wr.web.controller.module.GenATopicController.edit()', 'PUT', 1, 'admin', NULL, '/business/genATopic', '127.0.0.1', '内网IP', '{\"clickThePv\":0,\"clickTheUv\":0,\"content\":\"<p>为庆祝祖国73周年华诞，营造“欢度国庆，喜迎二十大，永远跟党走，奋进新征程”的北门老街文化氛围，城市运营公司于10月1日、3日、5日晚在北门老街举办“盛世华诞 举国同庆”歌舞晚会，首晚演出在歌曲《红旗飘飘》中拉开帷幕，11个节目精彩连连，现场气氛热烈，全程欢声笑语不断，进一步增强了北门老街商户和群众的获得感、幸福感。</p><p><img src=\\\"/prod-api/profile/upload/2022/10/31/戏曲3_20221031134639A038.png\\\"></p>\",\"contentMainImage\":\"/profile/upload/2022/10/31/戏曲2_20221031134616A037.png\",\"contentsTitle\":\"“盛世华诞 举国同庆”歌舞晚会\",\"createTime\":\"2022-10-31 13:46:42\",\"genId\":2,\"params\":{},\"postBy\":\"admin\",\"postDate\":\"2022-10-31 13:46:47\",\"postStatus\":\"2\",\"pushingArea\":\"0\",\"scenicIds\":\"1\",\"scenicList\":[\"1\"],\"scenicName\":\"老街景点\",\"updateBy\":\"admin\",\"updateTime\":\"2022-10-31 15:15:03\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 15:31:32');
