INSERT INTO `sys_oper_log` VALUES (2702, '商品管理', 1, 'com.wr.web.controller.module.CommodityController.add()', 'POST', 1, 'admin', NULL, '/business/commodity', '183.162.254.37', 'XX XX', '{\"businessCategory\":\"2\",\"businessHours\":[\"09:00:00\",\"22:30:00\"],\"commodityId\":3,\"commodityImages\":\"/profile/upload/2022/10/31/7b36d9d1f23d8ad40559530a2ca79336_20221031161626A007.jpeg\",\"commodityPrices\":\"25\",\"createBy\":\"admin\",\"goodsOrigin\":\"合肥\",\"nameOfCommodity\":\"草莓果果\",\"params\":{\"startTime\":\"09:00\",\"endTime\":\"22:30\"},\"productId\":\"87893275891\",\"productKeyDescription\":\"酸甜可口 富含多种维生素\",\"theIntroduction\":\"<p><span style=\\\"background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);\\\">“草莓又叫红莓、洋莓、地莓等,是一种红色的水果。草莓是对蔷薇科草莓属 植物的通称,属多年生草本植物。草莓的外观呈心形,鲜美红嫩,果肉多汁,含 有特殊的浓郁水果芳香。草莓营养价值高,含丰富维生素 C ,有帮助消化的功 效,与此同时,草莓还可以巩固齿龈,清新口气,润泽喉部。</span></p>\",\"theOpeningTime\":\"2022-10-11\",\"vendorId\":2}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 16:20:08');
