INSERT INTO `sys_oper_log` VALUES (2705, '商品管理', 1, 'com.wr.web.controller.module.CommodityController.add()', 'POST', 1, 'admin', NULL, '/business/commodity', '183.162.254.37', 'XX XX', '{\"businessCategory\":\"2\",\"businessHours\":[\"09:30:00\",\"21:00:00\"],\"commodityId\":4,\"commodityImages\":\"/profile/upload/2022/10/31/05bd943b8eb4dc8854b7b4c02d5ce20b_20221031162125A009.jpeg\",\"commodityPrices\":\"30\",\"createBy\":\"admin\",\"goodsOrigin\":\"合肥\",\"nameOfCommodity\":\"猕猴桃子\",\"params\":{\"startTime\":\"09:30\",\"endTime\":\"21:00\"},\"productId\":\"87893275891\",\"productKeyDescription\":\"美容养颜、增强体质\",\"theIntroduction\":\"<p>\\t<span style=\\\"background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);\\\">中华猕猴桃（学名：</span><em>\\t</em><em style=\\\"background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);\\\">Actinidia chinensis</em>\\t<span style=\\\"background-color: rgb(255, 255, 255); color: rgb(51, 51, 51);\\\">&nbsp;Planch.）是猕猴桃科、猕猴桃属大植物。大型落叶藤本；幼一枝或厚或薄地被有灰白色茸毛或褐色长硬毛或铁锈色硬毛状刺毛，老时秃净或留有断损残毛。叶纸质，倒阔卵形至倒卵形或阔卵形至近圆形，长6-17厘米，宽7-15厘米，顶端截平形并中间凹入或具突尖、急尖至短渐尖。聚伞花序1-3花，花序柄长7-15毫米，花柄长9-15毫米；苞片小，卵形或钻形，长约1毫米，均被灰白色丝状绒毛或黄褐色茸毛。果黄褐色，近球形、圆柱形、倒卵形或椭圆形，长4-6厘米，被茸毛、长硬毛或刺毛状长硬毛，成熟时秃净或不秃净，具小而多的淡褐色斑点；宿存萼片反折；种子纵径2.5毫米。</span></p>\",\"theOpeningTime\":\"2022-10-03\",\"vendorId\":2}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 16:23:59');
INSERT INTO `sys_oper_log` VALUES (2706, '商品管理', 1, 'com.wr.web.controller.module.CommodityController.add()', 'POST', 1, 'admin', NULL, '/business/commodity', '183.162.254.37', 'XX XX', '{\"businessCategory\":\"2\",\"businessHours\":[\"08:00:00\",\"22:00:00\"],\"commodityId\":5,\"commodityImages\":\"/profile/upload/2022/10/31/59a4c53391ba639be257d506a4120be5_20221031162407A010.jpeg\",\"commodityPrices\":\"30\",\"createBy\":\"admin\",\"goodsOrigin\":\"合肥\",\"nameOfCommodity\":\"香甜橙子\",\"params\":{\"startTime\":\"08:00\",\"endTime\":\"22:00\"},\"productId\":\"87893275891\",\"productKeyDescription\":\"补充维生素C 、生津止渴、促进消化、降低血脂的功效\",\"theIntroduction\":\"<p class=\\\"ql-align-justify\\\">橙子的功效与作用有生津止渴、促进消化、降低血脂的功效，还能够达到美容养颜的作用。</p><p class=\\\"ql-align-justify\\\">1、生津止渴：橙子中含有比较多的水分，而且还有大量的维生素能够帮助增强免疫功能，同时还能够起到生津止渴的功效，可以帮助改善口干舌燥的表现症状。</p><p class=\\\"ql-align-justify\\\">2、促进消化：橙子中的营养物质比较丰富，包括柠檬酸苹果酸含有较丰富的膳食纤维摄入之后，能够促使肠胃的蠕动，加快食物的消化和吸收，避免出现食物堆积引起腹胀腹痛等表现症状。</p><p class=\\\"ql-align-justify\\\">3、降低血脂：橙子中含有较多的果胶成分，能够将体内多余的胆固醇排出体外，减少胆固醇的吸收或者堆积，降低血脂，预防高血脂高血压等疾病。</p><p><br></p>\",\"theOpeningTime\":\"2022-10-16\",\"vendorId\":1}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 16:26:24');
