INSERT INTO `sys_oper_log` VALUES (2780, '餐饮管理', 2, 'com.wr.web.controller.module.RestaurantController.edit()', 'PUT', 1, 'admin', NULL, '/business/restaurant/edit', '183.162.254.37', 'XX XX', '{\"administrativeRegion\":\"1\",\"businessHours\":[\"13:00:00\",\"22:00:00\"],\"diningCapacityStr\":\"200\",\"endTime\":\"22:00:00\",\"hotelDescription\":\"<p>\\t火锅一般是指以锅为器具，以热源烧锅，以水或汤烧开来涮煮各类食物的烹调方式，同时亦可指这种烹调方式所用的锅具。其特色为边煮边吃，或是锅本身具有保温效果，吃的时候食物仍热气腾腾，汤物合一。世界各地均有类似的料理，但主要在东亚地方特别盛行。火锅现吃现烫，辣咸鲜，油而不腻，解郁除湿，适于山川之气候，今发展为<a href=\\\"https://baike.baidu.com/item/%E9%B8%B3%E9%B8%AF%E9%94%85/5631995?fromModule=lemma_inlink\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(19, 110, 194);\\\">鸳鸯锅</a>，麻辣、清淡各别，各取所需，根据个人的喜欢加不同的汤料、食物，老少皆宜，至冬之佳品。</p><p>\\t典型的火锅食材包括各种肉类、海鲜类、蔬菜类、豆制品类、菌菇类、蛋类制品、主食等，将其放入煮开的清水或特制的高汤锅底烫熟后食用。有些吃法还会蘸上调味料一起食用。</p>\",\"merchantContacts\":\"05517845365\",\"merchantKeyDescriptions\":\"坚持天然绿色理念，发扬胶东美食文化，做出顾客最满意的美味，成为顾客最需要的餐厅。\",\"merchantPhotos\":\"/profile/upload/2022/10/31/75c3f3cf62d7a15a29cdb6721299d064_20221031175920A037.jpeg,/profile/upload/2022/10/31/62b52b84650047edfbbdc31bf256dc53_20221031175924A038.jpeg,/profile/upload/2022/10/31/431826c15d9e7ea842cbbaec8052d525_20221031175927A039.jpeg\",\"merchantPhotosList\":[\"/profile/upload/2022/10/31/431826c15d9e7ea842cbbaec8052d525_20221031171624A005.jpeg\",\"/profile/upload/2022/10/31/75c3f3cf62d7a15a29cdb6721299d064_20221031171918A006.jpeg\",\"/profile/upload/2022/10/31/62b52b84650047edfbbdc31bf256dc53_20221031171921A007.jpeg\"],\"nameOfHotel\":\"捞一捞火锅\",\"openingHours\":\"2022-10-02\",\"params\":{\"startTime\":\"13:00\",\"endTime\":\"22:00\"},\"preciseLocation\":\"安徽省合肥市蜀山区井岗镇科学岛路39号公岗大院土菜馆\",\"pricePerCapita\":100,\"restaurantId\":8,\"startTime\":\"13:00:00\",\"typeOfCuisine\":\"4\",\"updateBy\":\"admin\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-10-31 17:59:28');
