INSERT INTO `sys_oper_log` VALUES (2796, '创A专题', 2, 'com.wr.web.controller.module.GenATopicController.edit()', 'PUT', 1, 'admin', NULL, '/business/genATopic', '183.162.254.37', 'XX XX', '{\"clickThePv\":0,\"clickTheUv\":0,\"content\":\"<p>秋十月，秋风送爽，在这喜庆祥和、硕果飘香的日子里，城市运营公司10月3日在北门老街举办“盛世华诞 举国同庆”系列活动—戏曲专场晚会，与市民共享喜悦，欢度国庆，喜迎二十大。演出邀请国家级非物质文化遗产皖南花鼓戏代表性传承人杨玉屏主导，在《戏曲大联唱》中拉开帷幕，用“打金枝”等经典曲目，生动演绎了当代戏剧传统文化的传承与创新，独具地方特色的表演风格及优美的声腔赢得了现场观众的阵阵掌声，给广大市民带来了一场视觉和听觉的盛宴。</p><p>为庆祝祖国73周年华诞，营造“欢度国庆，喜迎二十大，永远跟党走，奋进新征程”的北门老街文化氛围，城市运营公司于10月1日、3日、5日晚在北门老街举办“盛世华诞 举国同庆”歌舞晚会，首晚演出在歌曲《红旗飘飘》中拉开帷幕，11个节目精彩连连，现场气氛热烈，全程欢声笑语不断，进一步增强了北门老街商户和群众的获得感、幸福感。</p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/活动4.1_20221101093818A056.png\\\"></p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/活动4.2_20221101093826A057.png\\\"></p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/活动4.3_20221101093835A058.png\\\"></p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/活动4.4_20221101093844A059.png\\\"></p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/活动4.5_20221101093854A060.png\\\"></p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/活动4.6_20221101093905A061.png\\\"></p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/活动4.7_20221101093917A062.png\\\"></p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/活动4.8_20221101093934A063.png\\\"></p>\",\"contentMainImage\":\"/profile/upload/2022/10/31/戏剧_20221031132858A026.png\",\"contentsTitle\":\"“盛世华诞 举国同庆”系列活动—戏曲专场晚会\",\"createTime\":\"2022-10-31 13:30:51\",\"genId\":1,\"params\":{},\"postBy\":\"admin\",\"postDate\":\"2022-10-31 13:30:55\",\"postStatus\":\"2\",\"pushingArea\":\"0\",\"scenicIds\":\"1\",\"scenicList\":[\"1\"],\"scenicName\":\"老街景点\",\"updateBy\":\"admin\",\"updateTime\":\"2022-11-01 09:37:02\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-11-01 09:39:38');
