INSERT INTO `sys_oper_log` VALUES (2801, '项目文化', 2, 'com.wr.web.controller.module.ProjectCultureController.edit()', 'PUT', 1, 'admin', NULL, '/business/culture/edit', '183.162.254.37', 'XX XX', '{\"administrativeAreas\":\"0\",\"createBy\":\"admin\",\"culturalDescription\":\"<p class=\\\"ql-align-justify\\\">“从出生我就住在北门，小时候每次从繁华的市中心回到家里，都会有一种荒凉寂寞的感觉。”在90年出生的孙敏看来，这里已经褪去了过往的荣光，没有林立的商铺，也没有来往的车辆。“每次我想买个东西还要走好长一段路，才能到小超市。”老街区给孙敏最直观的感觉就是不方便。</p>\",\"cultureId\":1,\"cultureImages\":\"/profile/upload/2022/10/31/活动2.4_20221031132449A021.png\",\"cultureImagesList\":[\"/profile/upload/2022/10/31/活动2.4_20221031132449A021.png\"],\"params\":{},\"postStatus\":\"1\",\"projectId\":\"1\",\"projectName\":\"老北门\",\"theNameOfTheCulture\":\"艺术文化\",\"theSorting\":\"2\",\"updateBy\":\"admin\",\"updateTime\":\"2022-10-31 17:43:53\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-11-01 09:58:04');
