INSERT INTO `sys_oper_log` VALUES (2813, '活动发布', 1, 'com.wr.web.controller.module.EventReleaseController.add()', 'POST', 1, 'admin', NULL, '/business/eventRelease', '183.162.254.37', 'XX XX', '{\"content\":\"<p>九山半水半分田，山高沟深骆驼湾。十年前的骆驼湾村是个远近闻名的穷困山村，乡亲们走不上好路，住不进好房，种不了好田，收不着好粮。十年间，得益于中央脱贫攻坚重大部署，骆驼湾村在村党支部的带领下，在脱贫攻坚的道路上苦干实干，如今的骆驼湾村，山青青，水悠悠，村容景致优美，民居焕然一新，食用菌、高山林果种植和乡村旅游等产业初具规模，一半多农户家里有了小轿车，过上了名副其实的小康生活。</p>\",\"contentCategory\":\"2\",\"contentMainImage\":\"/profile/upload/2022/11/01/1129090645_16672595460911n_20221101101748A066.jpg\",\"contentSource\":\"人民网\",\"contentsTitle\":\"测试活动\",\"createBy\":\"admin\",\"dateRange\":[\"2022-11-01\",\"2022-12-31\"],\"endingDate\":\"2022-12-31 00:00:00\",\"eventId\":4,\"params\":{},\"participatingUser\":\"群众\",\"postSource\":\"1\",\"pushingArea\":\"0\",\"scenicIds\":\"1\",\"scenicList\":[\"1\"],\"startDate\":\"2022-11-01 00:00:00\",\"tripartiteUrl\":\"www.news.cn/2022-10/31/c_1129090645.htm\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-11-01 10:18:01');
