INSERT INTO `sys_oper_log` VALUES (2842, '餐饮管理', 2, 'com.wr.web.controller.module.RestaurantController.edit()', 'PUT', 1, 'admin', NULL, '/business/restaurant/edit', '183.162.254.37', 'XX XX', '{\"administrativeRegion\":\"0\",\"businessHours\":[\"09:00:00\",\"23:00:00\"],\"diningCapacityStr\":\"200\",\"endTime\":\"23:00:00\",\"hotelDescription\":\"<p>一、潮汕牛肉火锅成名与上世纪40年代，传统做法是将沙茶酱加入锅中,用浓汤做锅底，后来逐渐化繁为简，涮牛肉的锅底只剩下生骨清汤和白萝卜，更有利干尝出牛肉本来的味道，潮汕美食最讲究\'大味至淡’，从历史角度看，大都是悄无声息做减法的过程。潮汕的牛肉火锅，牛肉都是现宰现卖，把“鲜”字发挥到极致。牛肉部位不同，质地不同，需要涮的时间不同,这个涮牛肉的顺序也非常讲究:从瘦到肥并且必须是等所有鲜牛肉涮完才能下其他的食材，这样汤底才会保持清爽、鲜甜的口感。</p><p><br></p><p>二、每一道菜都有独特的做法和见解，而每一道菜的做法初心都是尽可能的保持原来的味道和鲜味，那潮汕的牛肉火锅牛很好把“鲜”字发挥到极致，牛肉必须在同一天宰杀牛肉，然后选择并分割，用筋膜切成薄片，仅可压碎整只牛的37%，其他较旧的部分打牛肉丸或生筋丸，牛肉火锅就像一条清澈的溪流，没有重油，没有浓郁的味道，并且正在寻找新鲜，原始和清淡养生的食物。</p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/图片16_20221101132138A127.png\\\"></p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/图片17_20221101132146A128.png\\\"></p><p><img src=\\\"/prod-api/profile/upload/2022/11/01/图片18_20221101132152A129.png\\\"></p>\",\"latitude\":\"30.955039\",\"longitude\":\"118.756826\",\"merchantContacts\":\"05633520677\",\"merchantKeyDescriptions\":\"清爽、鲜甜\",\"merchantPhotos\":\"/profile/upload/2022/10/31/潮牛掌柜-餐饮_20221031175940A040.png,/profile/upload/2022/11/01/图片12_20221101132101A122.png,/profile/upload/2022/11/01/图片13_20221101132107A123.png,/profile/upload/2022/11/01/图片14_20221101132110A124.png,/profile/upload/2022/11/01/图片15_20221101132129A126.png\",\"merchantPhotosList\":[\"/profile/upload/2022/10/31/潮牛掌柜-餐饮_20221031175940A040.png\",\"/profile/upload/2022/11/01/图片12_20221101132101A122.png\",\"/profile/upload/2022/11/01/图片13_20221101132107A123.png\",\"/profile/upload/2022/11/01/图片14_20221101132110A124.png\",\"/profile/upload/2022/11/01/图片15_20221101132129A126.png\"],\"nameOfHotel\":\"潮牛掌柜\",\"openingHours\":\"2022-01-31\",\"params\":{\"startTime\":\"09:00\",\"endTime\":\"23:00\"},\"preciseLocation\":\"安徽省宣城市宣州区澄江街道潮牛掌柜潮汕鲜牛火锅(北门老街店)\",\"pricePerCapita\":84,\"restaurantId\":7,\"startTime\":\"09:00:00\",\"typeOfCuisine\":\"1\",\"updateBy\":\"admin\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2022-11-01 13:46:49');
